INSERT INTO `music_info` VALUES ('368762740894', '100000000000', 'ltm__YP9TtHG18ZDseu15K', 'RFKKIwhYdLx7DfO', '七里香', '202508/Mh31WsvNPOLKCGWgdm9ROtPJ2lK9Wu.png', '202508/PWhi8tjEcCVux2WjJBkf2BAmSsTiwj.mp3', 187, '[{\"start\":9.321,\"end\":13.24,\"text\":\"窗外的麻雀在电线杆上多嘴\"},{\"start\":14.182,\"end\":17.95,\"text\":\"你说这一句很有夏天的感觉\"},{\"start\":19.202,\"end\":23.35,\"text\":\"手中的铅笔在纸上来来回回\"},{\"start\":24.103,\"end\":27.53,\"text\":\"我用几行字形容你是我的谁\"},{\"start\":29.063,\"end\":33.3,\"text\":\"秋刀鱼的滋味猫跟你都想了解\"},{\"start\":34.044,\"end\":37.85,\"text\":\"初恋的香味就这样被我们寻回\"},{\"start\":38.384,\"end\":42.655,\"text\":\"那温暖的阳光像刚摘的鲜艳草莓\"},{\"start\":42.705,\"end\":46.855,\"text\":\"你说你舍不得吃掉这一种感觉\"},{\"start\":46.905,\"end\":51.78,\"text\":\"雨下整夜我的爱溢出就像雨水\"},{\"start\":52.166,\"end\":57.036,\"text\":\"院子落叶跟我的思念厚厚一叠\"},{\"start\":57.086,\"end\":62.46,\"text\":\"几句是非也无法将我的热情冷却\"},{\"start\":62.887,\"end\":66.85,\"text\":\"你出现在我诗的每一页\"},{\"start\":67.207,\"end\":71.74,\"text\":\"雨下整夜我的爱溢出就像雨水\"},{\"start\":72.168,\"end\":76.5,\"text\":\"窗台蝴蝶像诗里纷飞的美丽章节\"},{\"start\":76.908,\"end\":78.54,\"text\":\"我接着写\"},{\"start\":78.928,\"end\":82.18,\"text\":\"把永远爱你写进诗的结尾\"},{\"start\":82.509,\"end\":86.499,\"text\":\"你是我唯一想要的了解\"},{\"start\":86.549,\"end\":91.21,\"text\":\"雨下整夜我的爱溢出就像雨水\"},{\"start\":91.61,\"end\":96.15,\"text\":\"院子落叶跟我的思念厚厚一叠\"},{\"start\":96.33,\"end\":101.16,\"text\":\"几句是非也无法将我的热情冷却\"},{\"start\":101.591,\"end\":105.15,\"text\":\"你出现在我诗的每一页\"},{\"start\":117.853,\"end\":122.15,\"text\":\"那饱满的稻穗幸福了这个季节\"},{\"start\":122.733,\"end\":127.25,\"text\":\"而你的脸颊像田里熟透的番茄\"},{\"start\":127.954,\"end\":133.11,\"text\":\"你突然对我说七里香的名字很美\"},{\"start\":133.434,\"end\":138.86,\"text\":\"我此刻却只想亲吻你倔强的嘴\"},{\"start\":139.035,\"end\":143.26,\"text\":\"雨下整夜我的爱溢出就像雨水\"},{\"start\":143.895,\"end\":148.36,\"text\":\"院子落叶跟我的思念厚厚一叠\"},{\"start\":148.716,\"end\":154.01,\"text\":\"几句是非也无法将我的热情冷却\"},{\"start\":154.357,\"end\":158.48,\"text\":\"你出现在我诗的每一页\"},{\"start\":158.817,\"end\":163.36,\"text\":\"整夜我的