INSERT INTO `music_info` VALUES ('607954654039', '100000000000', 'ltm__pP7T7gxef4Xy1gBZE', 'j6mzqUc09Okaemx', '旧时光的小镇 ', '202508/gfV5yVgCiuffCJtk4TNJyj5II5oBvg.png', '202508/whag6Md1d2gbKJd8ebQ2s2Fz8JY1US.mp3', 191, '[{\"start\":13.401,\"end\":15.6,\"text\":\"老街的石板曾经湿滑\"},{\"start\":16.002,\"end\":18.24,\"text\":\"巷口的奶茶冒着白花\"},{\"start\":18.582,\"end\":20.87,\"text\":\"小镇的黄昏带着喧哗\"},{\"start\":21.262,\"end\":23.42,\"text\":\"我们跑过笑声成了年华\"},{\"start\":23.842,\"end\":26.04,\"text\":\"院子里大树挂着秋千\"},{\"start\":26.423,\"end\":28.72,\"text\":\"风吹过竹林摇曳轻眠\"},{\"start\":29.043,\"end\":31.32,\"text\":\"童年的画面浮现眼前\"},{\"start\":31.663,\"end\":33.94,\"text\":\"像老旧照片泛黄依然\"},{\"start\":34.264,\"end\":36.52,\"text\":\"铁皮屋顶下雨声作伴\"},{\"start\":36.884,\"end\":39.15,\"text\":\"泥土芬芳藏在草丛间\"},{\"start\":39.464,\"end\":41.7,\"text\":\"池塘的涟漪荡开了远岸\"},{\"start\":42.104,\"end\":44.4,\"text\":\"小镇的故事唱不完一半\"},{\"start\":44.705,\"end\":47.03,\"text\":\"捉迷藏躲进长长弄堂\"},{\"start\":47.345,\"end\":49.59,\"text\":\"那时的时光从不漫长\"},{\"start\":49.825,\"end\":52.13,\"text\":\"每一个夏天都充满阳光\"},{\"start\":52.445,\"end\":54.116,\"text\":\"每一句承诺都无比坚强\"},{\"start\":54.166,\"end\":58.1,\"text\":\"记忆深处的小镇模样\"},{\"start\":59.386,\"end\":63.45,\"text\":\"日落铺满街头的希望\"},{\"start\":64.267,\"end\":69.52,\"text\":\"童年的欢笑是我的信仰\"},{\"start\":69.827,\"end\":74.2,\"text\":\"走得再远也不会遗忘\"},{\"start\":75.028,\"end\":79.1,\"text\":\"那古老屋檐下的风霜\"},{\"start\":79.908,\"end\":82.439,\"text\":\"刻在心底最深的地方\"},{\"start\":82.489,\"end\":83.999,\"text\":\"那些回忆像星辰发亮\"},{\"start\":84.049,\"end\":84.799,\"text\":\"指引我前行不惧彷徨\"},{\"start\":84.849,\"end\":85.919,\"text\":\"我们的小镇微风轻扬\"},{\"start\":85.969,\"end\":87.279,\"text\":\"老树下回忆散发清香\"},{\"start\":87.329,\"end\":90.3,\"text\":\"岁月流转却无法抵挡\"},{\"start\":90.689,\"end\":95.2,\"text\":\"那段纯真的旧时光\"},{\"start\":96.83,\"end\":99.21,\"text\":\"红砖墙外牵牛花盛开\"},{\"start\":99.49,\"end\":101.8,\"text\":\"小河边鱼儿跃出水面来\"},{\"start\":102.091,\"end\":104.29,\"text\":\"邻家的笑声穿过窗台\"},{\"start\":104.751,\"end\":106.56,\"text\"